package edu.tum.cs.i1.pse;public class InventorySystem {	private static String TALISKER = "Talisker";	private int totalStock;	private Warehouse warehouse = new WarehouseImpl();	public void addToWarehouse(String item, int amount) {		totalStock += amount;		warehouse.add(item, amount);	}		public int getTotalStock() {		return totalStock;	}		public boolean processOrder(Order order) {		order.fill(warehouse);		return order.isFilled();	}		public static void main(String[] args) {		InventorySystem inventorySystem = new InventorySystem();				inventorySystem.addToWarehouse(TALISKER, 50);				boolean order1success = inventorySystem.processOrder(new OrderImpl(TALISKER, 50));		boolean order2success = inventorySystem.processOrder(new OrderImpl(TALISKER, 51));				System.out.println("Order1 succeeded? " + order1success + " - Order2 succeeded? " + order2success);	}}